import React, { useEffect, useRef } from "react";
import * as THREE from "three";

export default function BlackjackCard({ width, height }) {
    const cardContainer = useRef(null);

    useEffect(() => {
        let scene, camera, renderer, card;

        // Create a new scene
        scene = new THREE.Scene();

        // Create a camera
        camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
        camera.position.z = 5;

        // Create a renderer
        renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(width, height);
        renderer.setClearColor(0xffffff, 1);

        // Append renderer to the container
        cardContainer.current.appendChild(renderer.domElement);

        // Create a card geometry (plane)
        const geometry = new THREE.PlaneGeometry(3, 4);
        const texture = new THREE.TextureLoader().load(
            "path_to_your_card_texture.jpg" // Replace with the path to your card texture
        );
        const material = new THREE.MeshBasicMaterial({ map: texture, side: THREE.DoubleSide });

        // Create the card mesh
        card = new THREE.Mesh(geometry, material);
        scene.add(card);

        // Animation function (if needed)

        // Render function
        const animate = () => {
            requestAnimationFrame(animate);

            // Any animation logic if required

            renderer.render(scene, camera);
        };

        animate();

        // Clean up on unmount
        return () => {
            renderer.domElement.remove();
            renderer.dispose();
        };
    }, [width, height]);

    return <div ref={cardContainer} />;
}
