import React, { useState } from "react";
import './LeftNav.css';
import logo from '../src/assets/logo.png';

export default function LeftNav({ setCurrentContent, setLoading }) {
    const [activeLink, setActiveLink] = useState(''); // State to track active link

    const handleClick = (content) => {
        setLoading(true);
        setCurrentContent(content);
        setActiveLink(content); // Update active link based on clicked content
    };

    return (
        <div className="left-nav-container">
            <a id='logo' href="./"><img id="logo" src={logo} alt="logo" /></a>

            <div className="links-container">
                <div className="links-middle">
                    {/* About Link */}
                    <div className={`links ${activeLink === 'about' ? 'active' : ''}`} onClick={() => handleClick('about')}>
                        <a href="#about" className="about-button">
                            {/* SVG */}
                        </a>
                    </div>

                    {/* Projects Link */}
                    <div className={`links ${activeLink === 'projects' ? 'active' : ''}`} onClick={() => handleClick('projects')}>
                        <a href="#projects" className="projects-button">
                            {/* SVG */}
                        </a>
                    </div>

                    {/* Contact Link */}
                    <div className={`links ${activeLink === 'contact' ? 'active' : ''}`} onClick={() => handleClick('contact')}>
                        <a href="#contact" className="contact-button">
                            {/* SVG */}
                        </a>
                    </div>
                </div>
            </div>
        </div>
    );
}
