import React, { useRef, useEffect } from 'react';
import * as THREE from 'three';

const Snowfall = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    let scene, camera, renderer, snowflakes;

    const createScene = () => {
      scene = new THREE.Scene();

      camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
      camera.position.z = 50;

      renderer = new THREE.WebGLRenderer({ antialias: true, canvas: canvasRef.current });
      renderer.setSize(window.innerWidth, window.innerHeight);

      const snowflakeGeometry = new THREE.SphereGeometry(0.5, 32, 32);
      const snowflakeMaterial = new THREE.MeshBasicMaterial({ color: 0xfff });

      snowflakes = [];

      for (let i = 0; i < 500; i++) {
        const snowflake = new THREE.Mesh(snowflakeGeometry, snowflakeMaterial);
        snowflake.position.x = Math.random() * 100 - 50;
        snowflake.position.y = Math.random() * 100 - 50;
        snowflake.position.z = Math.random() * 100 - 50;
        scene.add(snowflake);
        snowflakes.push(snowflake);
      }
    };

    const animate = () => {
      requestAnimationFrame(animate);

      snowflakes.forEach((snowflake) => {
        snowflake.position.y -= 0.1;

        if (snowflake.position.y < -50) {
          snowflake.position.y = 50;
          snowflake.position.x = Math.random() * 100 - 50;
          snowflake.position.z = Math.random() * 100 - 50;
        }
      });

      renderer.render(scene, camera);
    };

    createScene();
    animate();

    const handleWindowResize = () => {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    };

    window.addEventListener('resize', handleWindowResize);

    return () => {
      window.removeEventListener('resize', handleWindowResize);
      // Clean up Three.js resources when component unmounts
      scene.remove(...snowflakes);
      renderer.dispose();
    };
  }, []);

  return <canvas ref={canvasRef} />;
};

export default Snowfall;
