import React, { useRef, useEffect } from 'react';
import * as THREE from 'three';
import globeTexture from '../src/your-globe-texture.webp';
import './Globe.css';
import { render } from '@testing-library/react';

const Globe = () => {
  const scene = new THREE.Scene();
  const camera = new THREE.PerspectiveCamera( 70, window.innerWidth / window.innerHeight, 1, 1000);
  const renderer = new THREE.WebGLRenderer({ antialias: true }); // Enable antialiasing for smoother edges
  const globeRef = useRef(null);

  useEffect(() => {
    // Set up renderer
    globeRef.current.appendChild(renderer.domElement);

    // Create a sphere geometry for the globe with higher segments
    const geometry = new THREE.SphereGeometry(1, 64, 64); // Increased segments for a smoother surface
    const textureLoader = new THREE.TextureLoader();
    const texture = textureLoader.load(globeTexture); // Replace with your own texture
    scene.background = new THREE.Color('#ababab');

    // Use MeshPhongMaterial for better lighting and shading
    const material = new THREE.MeshPhongMaterial({ map: texture });
    const globeMesh = new THREE.Mesh(geometry, material);
    // Add lights to the scene (you can adjust positions and colors as needed)
    const ambientLight = new THREE.AmbientLight(0xffffff, 1);
    const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
    directionalLight.position.set(10, 1, 0);

    scene.add(globeMesh, ambientLight, directionalLight);
    const width = 100; // Adjust width and height as needed
    const height = 100;
    renderer.setSize(width, height); // Set size to match the desired dimensions

    // Set camera position
    camera.position.z = 2;

    // Animation loop
    const animate = () => {
      requestAnimationFrame(animate);
      globeMesh.rotation.y += 0.005;
      renderer.render(scene, camera);
    };

    animate();

    // Clean up on component unmount
    return () => {
      globeRef.current.removeChild(renderer.domElement);
      renderer.dispose();
    };
  }, [renderer, scene, camera]);

  return <div className='globe' ref={globeRef} />;
};

export default Globe;
