import React, { useState } from 'react';
import AboutContent from './AboutContent';
import ProjectsContent from './ProjectsContent';
import ContactContent from './ContactContent';
import './Portfolio.css';


export default function Portfolio() {
    const [currentContent, setCurrentContent] = useState('about');

    const showAbout = () => setCurrentContent('about');
    const showProjects = () => setCurrentContent('projects');
    const showContact = () => setCurrentContent('contact');

    return (
        <div className="portfolio-container">
            <div className="buttons">
                <button onClick={showAbout}>About</button>
                <button onClick={showProjects}>Projects</button>
                <button onClick={showContact}>Contact</button>
            </div>

            <div className="content">
                {currentContent === 'about' && <AboutContent />}
                {currentContent === 'projects' && <ProjectsContent />}
                {currentContent === 'contact' && <ContactContent />}
            </div>
        </div> 
    )
}
