import React, { useState } from 'react';


const toppings = ['ham', 'pepporoni', 'sausage', 'pineapple', 'chicken'];


export default function Hero() {
  const [topping, setTopping] = useState([]);

  const addTopping = ({target}) =>{
    const selectedTopping = target.value;

    setTopping((prev) =>{
      if(prev.contains(selectedTopping)){
        return prev.filter(t => t !== selectedTopping);
      } else{
        return [topping, ...prev];
      }
    })
  }
  return(
    <>
      <h1>{toppings}</h1>
    </>
  )
}
