import React, { useRef } from 'react';
import { Canvas, useFrame } from 'react-three-fiber';
import * as THREE from 'three';

const GoldenRatioSpiral = () => {
  const spiralPoints = useRef([]);
  const line = useRef();

  // Function to calculate points for the golden ratio spiral
  const calculateSpiralPoints = () => {
    const points = [];
    const a = 1.61803398875; // Golden ratio

    for (let i = 0; i < 1000; i++) {
      const angle = i * 0.1;
      const radius = a ** i * 0.01;

      const x = radius * Math.cos(angle);
      const y = radius * Math.sin(angle);
      const z = i * 0.01;

      points.push(new THREE.Vector3(x, y, z));
    }

    spiralPoints.current = points;
  };

  // Calculate spiral points on mount
  calculateSpiralPoints();

  // Custom hook to update the line position
  useFrame(() => {
    const time = window.performance.now() / 1000;

    if (spiralPoints.current.length > 0 && line.current) {
      const index = Math.floor(time * 0.5) % spiralPoints.current.length;
      const currentPosition = spiralPoints.current[index];

      const vertices = line.current.geometry.attributes.position.array;
      vertices[0] = 0; // Starting point at the center
      vertices[1] = 0;
      vertices[2] = 0;
      vertices[3] = currentPosition.x;
      vertices[4] = currentPosition.y;
      vertices[5] = currentPosition.z;
      line.current.geometry.attributes.position.needsUpdate = true;
    }
  });

  return (
    <line ref={line}>
      <bufferGeometry attach="geometry" />
      <lineBasicMaterial color={0xff0000} />
    </line>
  );
};

const ThreeDComponent = () => {
  return (
    <Canvas>
      <ambientLight />
      <pointLight position={[10, 10, 10]} />
      <GoldenRatioSpiral />
    </Canvas>
  );
};

export default ThreeDComponent;
